#include <stdio.h>
#include <string.h>
int main() {
    char str[] = "abcd";
    for(int i = 0; i < strlen(str)-1; i += 2) {
        char temp = str[i];
        str[i] = str[i+1];
        str[i+1] = temp;
    }
    printf("%s", str);
    return 0;
}

#include <stdio.h>
int main() {
    int arr[3][3] = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};
    int sum = 0;
    for(int i = 0; i < 3; i++) for(int j = 0; j < i; j++) sum += arr[i][j];
    printf("%d", sum);
    return 0;
}

#include <stdio.h>
#include <string.h>
int main() {
    char str[] = "sky fly my";
    int max = 0, current = 0;
    for(int i = 0; i <= strlen(str); i++) {
        if(str[i] == ' ' || str[i] == '\0') {
            if(current > max) max = current;
            current = 0;
        } else if(str[i] != 'a' && str[i] != 'e' && str[i] != 'i' && str[i] != 'o' && str[i] != 'u') current++;
        else current = 0;
    }
    printf("%d", max);
    return 0;
}

