#include <iostream>
using namespace std;

class BankAccount {
private:
    string accountNumber;
    double balance;

public:
    BankAccount(string accNum, double initialBalance) {
        accountNumber = accNum;
        balance = (initialBalance >= 0) ? initialBalance : 0;
    }

    void deposit(double amount) {
        if (amount > 0) {
            balance += amount;
            cout << "Deposited: " << amount << endl;
        }
    }

    void withdraw(double amount) {
        if (amount > 0 && amount <= balance) {
            balance -= amount;
            cout << "Withdrawn: " << amount << endl;
        }
    }

    double getBalance() const {
        return balance;
    }
};

int main() {
    BankAccount acc("12345", 1000.0);
    acc.deposit(500.0);
    acc.withdraw(200.0);
    cout << "Balance: " << acc.getBalance() << endl;
    return 0;
}

#include <iostream>
using namespace std;

class Shape {
public:
    virtual double calculateArea() const = 0; // Pure virtual function
    virtual ~Shape() {}
};

class Circle : public Shape {
private:
    double radius;
public:
    Circle(double r) : radius(r) {}
    double calculateArea() const override {
        return 3.14159 * radius * radius;
    }
};

class Rectangle : public Shape {
private:
    double width, height;
public:
    Rectangle(double w, double h) : width(w), height(h) {}
    double calculateArea() const override {
        return width * height;
    }
};

int main() {
    Shape* shapes[] = {new Circle(5.0), new Rectangle(4.0, 6.0)};
    for (int i = 0; i < 2; i++) {
        cout << "Area: " << shapes[i]->calculateArea() << endl;
        delete shapes[i];
    }
    return 0;
}

#include <iostream>
#include <string>
using namespace std;

class Employee {
private:
    string id;
    double salary;

public:
    Employee(string empId, double empSalary) {
        id = empId;
        salary = empSalary;
    }

    void setSalary(double newSalary) {
        if (newSalary >= 0) salary = newSalary;
    }

    double getSalary() const {
        return salary;
    }

    string getId() const {
        return id;
    }
};

int main() {
    Employee emp("E001", 50000.0);
    cout << "ID: " << emp.getId() << ", Salary: " << emp.getSalary() << endl;
    emp.setSalary(60000.0);
    cout << "Updated Salary: " << emp.getSalary() << endl;
    return 0;
}

#include <iostream>
using namespace std;

class IPrinter {
public:
    virtual void print() const = 0;
    virtual ~IPrinter() {}
};

class IScanner {
public:
    virtual void scan() const = 0;
    virtual ~IScanner() {}
};

class MultifunctionDevice : public IPrinter, public IScanner {
public:
    void print() const override {
        cout << "Printing document..." << endl;
    }
    void scan() const override {
        cout << "Scanning document..." << endl;
    }
};

int main() {
    MultifunctionDevice device;
    IPrinter* printer = &device;
    IScanner* scanner = &device;
    printer->print();
    scanner->scan();
    return 0;
}

#include <iostream>
#include <string>
using namespace std;

class Student {
private:
    string name;
    double grade;

public:
    Student(string studentName, double studentGrade) {
        name = studentName;
        grade = (studentGrade >= 0 && studentGrade <= 100) ? studentGrade : 0;
    }

    void updateGrade(double newGrade) {
        if (newGrade >= 0 && newGrade <= 100) {
            grade = newGrade;
            cout << "Grade updated to: " << grade << endl;
        }
    }

    string getName() const {
        return name;
    }

    double getGrade() const {
        return grade;
    }
};

int main() {
    Student student("Alice", 85.5);
    cout << "Name: " << student.getName() << ", Grade: " << student.getGrade() << endl;
    student.updateGrade(92.0);
    return 0;
}

#include <iostream>
using namespace std;

class Vehicle {
public:
    virtual double calculateSpeed() const = 0; // Pure virtual function
    virtual ~Vehicle() {}
};

class Car : public Vehicle {
private:
    double distance, time;
public:
    Car(double d, double t) : distance(d), time(t) {}
    double calculateSpeed() const override {
        return (time > 0) ? distance / time : 0;
    }
};

class Bicycle : public Vehicle {
private:
    double distance, time;
public:
    Bicycle(double d, double t) : distance(d), time(t) {}
    double calculateSpeed() const override {
        return (time > 0) ? distance / time : 0;
    }
};

int main() {
    Vehicle* vehicles[] = {new Car(100.0, 2.0), new Bicycle(20.0, 1.0)};
    for (int i = 0; i < 2; i++) {
        cout << "Speed: " << vehicles[i]->calculateSpeed() << " units/hour" << endl;
        delete vehicles[i];
    }
    return 0;
}

#include <iostream>
using namespace std;

class Inventory {
private:
    int itemCount;
    double totalValue;

public:
    Inventory(int count, double value) {
        itemCount = (count >= 0) ? count : 0;
        totalValue = (value >= 0) ? value : 0;
    }

    void addItem(int quantity, double value) {
        if (quantity >= 0 && value >= 0) {
            itemCount += quantity;
            totalValue += value * quantity;
        }
    }

    int getItemCount() const {
        return itemCount;
    }

    double getTotalValue() const {
        return totalValue;
    }
};

int main() {
    Inventory inv(10, 50.0);
    inv.addItem(5, 10.0);
    cout << "Items: " << inv.getItemCount() << ", Total Value: " << inv.getTotalValue() << endl;
    return 0;
}

#include <iostream>
using namespace std;

class IAudioPlayer {
public:
    virtual void playAudio() const = 0;
    virtual ~IAudioPlayer() {}
};

class IVideoPlayer {
public:
    virtual void playVideo() const = 0;
    virtual ~IVideoPlayer() {}
};

class MediaDevice : public IAudioPlayer, public IVideoPlayer {
public:
    void playAudio() const override {
        cout << "Playing audio..." << endl;
    }
    void playVideo() const override {
        cout << "Playing video..." << endl;
    }
};

int main() {
    MediaDevice device;
    IAudioPlayer* audio = &device;
    IVideoPlayer* video = &device;
    audio->playAudio();
    video->playVideo();
    return 0;
}
