using System;
using System.IO;
using System.Runtime.Serialization.Formatters.Binary;

[Serializable]
class Person
{
    public string Name { get; set; }
    public int Age { get; set; }
}

class Program
{
    static void Main()
    {
        Person person = new Person { Name = "John", Age = 30 };
        BinaryFormatter formatter = new BinaryFormatter();
        using (FileStream fs = new FileStream("person.bin", FileMode.Create))
        {
            formatter.Serialize(fs, person);
        }
        Console.WriteLine("Object serialized.");
    }
}

using System;
using System.IO;
using System.Text.Json;

class Student
{
    public string Name { get; set; }
    public int Grade { get; set; }
}

class Program
{
    static void Main()
    {
        Student student = new Student { Name = "Alice", Grade = 85 };

        // Writing to JSON
        string jsonString = JsonSerializer.Serialize(student);
        File.WriteAllText("student.json", jsonString);

        // Reading from JSON
        string jsonRead = File.ReadAllText("student.json");
        Student studentRead = JsonSerializer.Deserialize<Student>(jsonRead);
        Console.WriteLine($"Read: {studentRead.Name}, {studentRead.Grade}");
    }
}

