using System;
using System.IO;

class Program
{
    static void Main()
    {
        string filePath = "document.txt";
        try
        {
            if (File.Exists(filePath))
            {
                FileAttributes attributes = File.GetAttributes(filePath);
                Console.WriteLine($"File: {filePath}");
                Console.WriteLine($"ReadOnly: {(attributes & FileAttributes.ReadOnly) == FileAttributes.ReadOnly}");
                Console.WriteLine($"Hidden: {(attributes & FileAttributes.Hidden) == FileAttributes.Hidden}");
                Console.WriteLine($"Archive: {(attributes & FileAttributes.Archive) == FileAttributes.Archive}");
            }
            else
            {
                Console.WriteLine("File does not exist.");
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

using System;
using System.IO;

class Program
{
    static void Main()
    {
        string source = "source.bin";
        string dest = "destination.bin";
        try
        {
            using (FileStream fsSource = new FileStream(source, FileMode.Open, FileAccess.Read))
            using (BufferedStream bsSource = new BufferedStream(fsSource))
            using (FileStream fsDest = new FileStream(dest, FileMode.Create, FileAccess.Write))
            using (BufferedStream bsDest = new BufferedStream(fsDest))
            {
                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = bsSource.Read(buffer, 0, buffer.Length)) > 0)
                {
                    bsDest.Write(buffer, 0, bytesRead);
                }
            }
            Console.WriteLine("File copied successfully.");
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

