using System;
using System.IO;
using System.IO.MemoryMappedFiles;

class Program
{
    static void Main()
    {
        string filePath = "largefile.bin";
        try
        {
            using (MemoryMappedFile mmf = MemoryMappedFile.CreateFromFile(filePath, FileMode.Open))
            using (MemoryMappedViewAccessor accessor = mmf.CreateViewAccessor(0, 100))
            {
                byte[] data = new byte[100];
                accessor.ReadArray(0, data, 0, data.Length);
                Console.WriteLine($"First 100 bytes: {BitConverter.ToString(data)}");
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

using System;
using System.IO;
using System.IO.Compression;
using System.Runtime.Serialization.Formatters.Binary;

[Serializable]
class Data
{
    public string Message { get; set; } = "Compressed data example";
}

class Program
{
    static void Main()
    {
        Data data = new Data();
        try
        {
            using (FileStream fs = new FileStream("data.gz", FileMode.Create))
            using (GZipStream gzip = new GZipStream(fs, CompressionMode.Compress))
            {
                BinaryFormatter formatter = new BinaryFormatter();
                formatter.Serialize(gzip, data);
            }
            Console.WriteLine("Object serialized and compressed.");
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

