using System;
using System.IO;
using System.IO.Compression;
using System.Runtime.Serialization.Formatters.Binary;

[Serializable]
class Data
{
    public string Message { get; set; }
}

class Program
{
    static void Main()
    {
        try
        {
            using (FileStream fs = new FileStream("data.gz", FileMode.Open))
