                Console.WriteLine($"Deserialized: {data.Message}");
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

using System;
using System.IO;
using System.Text.Json;

class Settings
{
    public string Mode { get; set; }
    public int Level { get; set; }
}

class Program
{
    static void Main()
    {
        try
        {
            string jsonString = File.ReadAllText("settings.json");
            Settings settings = JsonSerializer.Deserialize<Settings>(jsonString);
            settings.Level = 10; // Modify property
            string updatedJson = JsonSerializer.Serialize(settings, new JsonSerializerOptions { WriteIndented = true });
            File.WriteAllText("settings.json", updatedJson);
            Console.WriteLine($"Updated settings: Level = {settings.Level}");
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
        }
    }
}

using System;
using System.IO;

class Program
{
    static void Main()
    {
        using (FileSystemWatcher watcher = new FileSystemWatcher())
        {
            watcher.Path = Directory.GetCurrentDirectory();
            watcher.Filter = "*.txt";
            watcher.NotifyFilter = NotifyFilters.LastWrite | NotifyFilters.FileName;
            watcher.Changed += (sender, e) => Console.WriteLine($"File changed: {e.FullPath}");
            watcher.Created += (sender, e) => Console.WriteLine($"File created: {e.FullPath}");
            watcher.Deleted += (sender, e) => Console.WriteLine($"File deleted: {e.FullPath}");
            watcher.EnableRaisingEvents = true;
            Console.WriteLine("Monitoring started. Press Enter to stop.");
            Console.ReadLine();
        }
    }
}

