using System;
using System.Threading;

class Program {
    static void Main() {
        Thread t = new Thread(() => { Console.WriteLine("Thread started"); });
        t.Start();
        t.Join();
        Console.WriteLine("Main thread finished");
    }
}

using System;
using System.Threading;

class Program {
    static void Main() {
        Thread t1 = new Thread(PrintMessage);
        Thread t2 = new Thread(PrintMessage);
        t1.Start();
        t2.Start();
