using System;
using System.Threading;

class Program {
    static void Main() {
        Thread t = new Thread(Count);
        t.Start();
    }
    static void Count() {
        for (int i = 1; i <= 5; i++) {
            Console.WriteLine(i);
            Thread.Sleep(1000);
        }
    }
}

using System;
using System.Threading;

class Program {
    static void Main() {
        Thread t = new Thread(PrintNumbers);
        t.Start();
        Console.WriteLine("Main thread");
    }
    static void PrintNumbers() {
        for (int i = 1; i <= 3; i++) {
            Console.WriteLine(i);
        }
    }
}

using System;
using System.Threading;

class Program {
    static void Main() {
        Thread t1 = new Thread(WriteNumbers);
        Thread t2 = new Thread(WriteNumbers);
        t1.Start();
        t2.Start();
    }
    static void WriteNumbers() {
        for (int i = 1; i <= 3; i++) {
            Console.WriteLine(i);
            Thread.Sleep(500);
        }
    }
}

using System;
using System.Threading;

class Program {
    static void Main() {
        Task t1 = Task.Run(() => { Console.WriteLine("Task 1"); });
        Task t2 = Task.Run(() => { Console.WriteLine("Task 2"); });
        Task.WhenAll(t1, t2).Wait();
    }
}

